import "core-js/modules/es6.function.name";

/**
 * Vue Router
 *
 * @library
 *
 * https://router.vuejs.org/en/
 */
// Lib imports
import Vue from 'vue';
import VueAnalytics from 'vue-analytics';
import Router from 'vue-router';
import Meta from 'vue-meta'; // Routes

import paths from "./paths";

function route(path, view, name) {
  return {
    name: name || view,
    path: path,
    // component: (resolve) => import(`/* webpackChunkName: ${view}*/`  `@/views/console/views/${view}.vue`).then(resovle)
    component: function component(resovle) {
      return import("@/views/console/views/".concat(view, ".vue")).then(resovle);
    }
  };
}

Vue.use(Router); // Create a new router

var router = new Router({
  mode: 'history',
  routes: paths.map(function (path) {
    return route(path.path, path.view, path.name);
  }).concat([{
    path: '*',
    redirect: '/console/dashboard'
  }]),
  scrollBehavior: function scrollBehavior(to, from, savedPosition) {
    if (savedPosition) {
      return savedPosition;
    }

    if (to.hash) {
      return {
        selector: to.hash
      };
    }

    return {
      x: 0,
      y: 0
    };
  }
});
Vue.use(Meta); // Bootstrap Analytics
// Set in .env
// https://github.com/MatteoGabriele/vue-analytics

if (process.env.GOOGLE_ANALYTICS) {
  Vue.use(VueAnalytics, {
    id: process.env.GOOGLE_ANALYTICS,
    router: router,
    autoTracking: {
      page: process.env.NODE_ENV !== 'development'
    }
  });
}

export default router;
