export default {
  mounted(){
    // console.log(this.$route.path)
    this.activeTab = this.$route.path==='/register'?'register':'login'
  },
  data: () => ({
    items: [
        {
          src: 'https://cdn.vuetifyjs.com/images/carousel/squirrel.jpg'
        },
        {
          src: 'https://cdn.vuetifyjs.com/images/carousel/sky.jpg'
        }
      ],
    activeTab:'',
    l_valid: true,
    l_email: '111@163.com',
    l_emailRules: [
      v => !!v || 'E-mail is required',
      v => /.+@.+/.test(v) || 'E-mail must be valid'
    ],
    l_password: '123456',
    l_passwordRules: [
      v => !!v || 'Password is required',
      v => (v && v.length <= 16) || 'Password must be less than 16 characters'
    ],
    l_checkbox: true,
    r_valid: true,
    r_email: 'test@163.com',
    r_emailRules: [
      v => !!v || 'E-mail is required',
      v => /.+@.+/.test(v) || 'E-mail must be valid'
    ],
    r_password: '111',
    r_passwordRules: [
      v => !!v || 'Password is required',
      v => (v && v.length <= 16) || 'Password must be less than 16 characters'
    ],
    r_password2: '111',
    r_code:'1111',
    r_codeRules: [
      v => !!v || 'Code is required'
    ],
    r_checkbox: false
  }),
  computed: {
    r_password2Rules () {
      const rules = [
        v => !!v || 'Password2 is required',
        v => (v && v.length <= 16) || 'Password must be less than 16 characters',
        v => (v && v===this.r_password) || 'Inconsistent password input twice'
      ]
      return rules
    }
  },
  watch:{
    '$route.path':{
      handler(val){
        val==='/register'?this.activeTab='register':this.activeTab='login'
      },
      deep:true
    },
    activeTab(val){
      val==='register'?this.$router.push('register'):this.$router.push('login')
    }
  },
  methods: {
    ForgotPassword(){
      console.log('forget')
    },
    sendCode(){
      console.log('send code')
    },
    validate (form) {
      if (this.$refs[form].validate()) {
        this.$router.push("/")
      }
    },
    reset (form) {
      this.$refs[form].reset()
    }
  }
}
