export default {
    data: () => ({
      activeTab:'login',
      l_valid: true,
      l_telephone: '',
      l_telephoneRules: [
        v => !!v || 'Telephone is required',
        v => (v && v.length <= 10) || 'Telephone must be less than 10 characters'
      ],
      l_email: '',
      l_emailRules: [
        v => !!v || 'E-mail is required',
        v => /.+@.+/.test(v) || 'E-mail must be valid'
      ],
      l_checkbox: false,
      r_valid: true,
      r_telephone: '',
      r_telephoneRules: [
        v => !!v || 'Telephone is required',
        v => (v && v.length <= 10) || 'Telephone must be less than 10 characters'
      ],
      r_email: '',
      r_emailRules: [
        v => !!v || 'E-mail is required',
        v => /.+@.+/.test(v) || 'E-mail must be valid'
      ],
      r_checkbox: false
    }),

    methods: {
      validate () {
        if (this.$refs['r_form'].validate()) {
          this.snackbar = true
        }
      },
      reset () {
        this.$refs['r_form'].reset()
      },
      resetValidation () {
        this.$refs['r_form'].resetValidation()
      }
    }
  }
